using FormulaEvaluator;var formula = new Formula("double var1=0; double result=0; " +                          "if({power}==2) {var1=94.000;} " +                          "else if({power}>2 & {power}<5) {var1=110.000;} " +                          "else if({power}>4& {power}<7) {var1=140.000;} " +                          "else if({power}>6 & {power}<11) {var1=170.000;} " +                          "else if({power}>10 & {power}<15) {var1=220.000;} " +                          "else {var1=264.000;} if ({bonusMalus}==1){ result=var1*0.7; } " +                          "else if ({bonusMalus}==2){ result=var1*0.8; } " +                          "else if ({bonusMalus}==3){ result=var1*0.9; } " +                          "else if ({bonusMalus}==4){ result=var1; } " +                          "else if ({bonusMalus}==5){ result=var1*1.2; } " +                          "else if ({bonusMalus}==6){ result=var1*1.4; } " +                          "else if ({bonusMalus}==7){ result=var1*1.6; } " +                          "else if ({bonusMalus}==8){ result=var1*2; } " +                          "else if ({bonusMalus}==9){ result=var1*2.5; } " +                          "else if ({bonusMalus}==10){ result=var1*3; } else { result=var1*3.5; } return result;");var result = await formula.Evaluate();Console.WriteLine("Formula result = " + result);